mapper.update:
update jobinfo
set zwid=#{zwid},lxMan=#{lxMan},lxPhone=#{lxPhone},zpNum=#{zpNum},lxAddress=#{lxAddress},xueliRequire=#{xueliRequire},zhiyeType=#{zhiyeType},gzExperience=#{gzExperience},majorRequire=#{majorRequire},gzAddress=#{gzAddress},salaryLow=#{salaryLow},welfare=#{welfare},jobDuty=#{jobDuty},time=#{time},
salaryHigh=#{salaryHigh},prid=#{prid},ctid=#{ctid},sex=#{sex},ageLow=#{ageLow},ageHigh=#{ageHigh}, beginTime=#{beginTime}, endTime=#{endTime},dayTime=#{dayTime} , weekTime=#{weekTime},workTime=#{workTime},baoxian=#{baoxian}, gjijin=#{gjijin},jiangjin=#{jiangjin}, zhusu=#{zhusu}
where zpxxid=#{zpxxid};

mapper.select:
select C.type,B.*,A.* from
(select *,concat(beginTime,"至",endTime) zhaopinTime,concat(ageLow,"-",ageHigh) age,concat(salaryLow,"-",salaryHigh) salary,concat(baoxian,"、",gjijin,"、",jiangjin,"、",zhusu,"、",welfare) xinzifuli
from jobinfo where qyid=#{qyid}) A,station B,position C


			A.zwid=B.stid and B.poid=C.poid


			and jobinfoState=#{jobinfoState}


			and zwid=#{zwid}


			and C.type=#{type}


			and A.zpxxid=#{zpxxid}
